'0')};\n\n  ${mediaqueries.phablet`\n    margin-left: 0;\n  `}\n`;\n\n/* Gavin 20200722: Reduced bottom margin on the Header because it looked too\n  big w/ no hero */\nconst Header = styled.header`\n  position: relative;\n  z-index: 10;\n  margin:50px auto 120px;\n  padding-left: 68px;\n  max-width: 749px;\n\n  ${mediaqueries.desktop`\n    padding-left: 53px;\n    max-width: calc(507px + 53px);\n    margin: 50px auto 70px;\n  `}\n\n  ${mediaqueries.tablet`\n    padding-left: 0;\n    margin: 50px auto 70px;\n    max-width: 480px;\n  `}\n\n  ${mediaqueries.phablet`\n    margin: 50px auto 180px;\n    padding: 0 40px;\n  `}\n\n  @media screen and (max-height: 700px) {\n    margin: 50px auto;\n  }\n`;\n\nconst HeroHeading = styled(Headings.h1)`\n  font-size: 48px;\n  font-family: ${p => p.theme.fonts.serif};\n  margin-bottom: 25px;\n  font-weight: bold;\n  line-height: 1.32;\n\n  ${mediaqueries.tablet`\n    margin-bottom: 20px;\n    font-size: 36px;\n  `}\n\n  ${mediaqueries.phablet`\n    font-size: 32px;\n  `}\n`;\n\nconst HeroSubtitle = styled.div<{ hasCoAUthors: boolean }>`\n  position: relative;\n  display: flex;\n  font-size: 18px;\n  color: ${p => p.theme.colors.grey};\n\n  ${p => mediaqueries.phablet`\n    font-size: 14px;\n    flex-direction: column;\n\n    ${p.hasCoAUthors &&\n      `\n        &::before {\n          content: '';\n          position: absolute;\n          left: -20px;\n          right: -20px;\n          top: -10px;\n          bottom: -10px;\n          border: 1px solid ${p.theme.colors.horizontalRule};\n          opacity: 0.5;\n          border-radius: 5px;\n        }\n    `}\n\n\n    strong {\n      display: block;\n      font-weight: 500;\n      margin-bottom: 5px;\n    }\n  `}\n`;\n\n/* Gavin 20200724: Changed from 'height: 220px' to 'max-width: 100%' for phablet */\nconst HeroImage = styled.div`\n  position: relative;\n  z-index: 1;\n  width: 100%;\n  max-width: 944px;\n  overflow: hidden;\n  margin: 0 auto;\n  box-shadow: 0 30px 60px -10px r